function buildController( data ) {

				let geometry, material;

				switch ( data.targetRayMode ) {

					case 'tracked-pointer':

						geometry = new THREE.BufferGeometry();
						geometry.setAttribute( 'position', new THREE.Float32BufferAttribute( [ 0, 0, 0, 0, 0, - 1 ], 3 ) );
						geometry.setAttribute( 'color', new THREE.Float32BufferAttribute( [ 0.5, 0.5, 0.5, 0, 0, 0 ], 3 ) );

						material = new THREE.LineBasicMaterial( { vertexColors: true, blending: THREE.AdditiveBlending } );

						return new THREE.Line( geometry, material );

					case 'gaze':

						geometry = new THREE.RingBufferGeometry( 0.02, 0.04, 32 ).translate( 0, 0, - 1 );
						material = new THREE.MeshBasicMaterial( { opacity: 0.5, transparent: true } );
						return new THREE.Mesh( geometry, material );

				}

			}


    function mousemove(event){
    	mouse.x = ( event.clientX / player.width ) * 2 - 1;
    	mouse.y = - ( event.clientY / player.height ) * 2 + 1;
    }

    function update( event ) {

		if ( controller.userData.isSelecting === true ) {
			box.material.color.set( 0xff0000 );
		}



    	//raycaster.setFromCamera(mouse, camera);

		// find intersections

		tempMatrix.identity().extractRotation( controller.matrixWorld );

		raycaster.ray.origin.setFromMatrixPosition( controller.matrixWorld );
		raycaster.ray.direction.set( 0, 0, - 1 ).applyMatrix4( tempMatrix );

    	intersects = raycaster.intersectObjects(items.children);
    	//if (intersects.length !== 0) {
    	//	let obj = intersects[0].object;
    		//obj.material.color.set( 0xff0000 );
    	//}

		//for ( let i = 0; i < intersects.length; i ++ ) {
			//intersects[ i ].object.material.emissive.setHex( 0xff0000 );
		//}

		if ( intersects.length > 0 ) {

			if ( INTERSECTED != intersects[ 0 ].object ) {

				if ( INTERSECTED ) INTERSECTED.material.emissive.setHex( INTERSECTED.currentHex );

				INTERSECTED = intersects[ 0 ].object;
				INTERSECTED.currentHex = INTERSECTED.material.emissive.getHex();
				INTERSECTED.material.emissive.setHex( 0xff0000 );

			}

		} else {

			if ( INTERSECTED ) INTERSECTED.material.emissive.setHex( INTERSECTED.currentHex );

			INTERSECTED = undefined;

		}
    }
